mapper.select:
SELECT t_job.id,title, company, description, how_to_apply, city, country, is_public, is_expiry,
category.id category_id,category.name category_name,
type.id job_type_id,type.name type_name
FROM t_job
LEFT JOIN (SELECT id,name FROM t_category) category on category.id = t_job.category_id
LEFT JOIN (SELECT id,name FROM t_type) type on type.id = t_job.job_type_id


        and job_type_id = #{typeId}


        and category_id = #{categoryId}

    and title like #{content}
    or company like #{content}
    or description like #{content}

limit #{start},#{pageSize}

SELECT count(1) FROM t_job


        and job_type_id = #{typeId}


        and category_id = #{categoryId}

    and title like #{content}
    or company like #{content}
    or description like #{content}
